type Variant = "pro" | "community" | "ti-m";

interface ElementLogoProps extends React.ComponentProps<"div"> {
  variant: Variant;
}

/** The Element logo, reconstructed in three parts: the round logo, the text part and the badge */
export const ElementLogo: React.FC<ElementLogoProps> = ({
  variant,
  ...props
}: ElementLogoProps) => {
  const intl = useIntl();
  let title;
  let BadgePart;
  switch (variant) {
    case "pro": {
      title = intl.formatMessage({
        id: "product.title.pro",
        defaultMessage: "Element Pro",
        description: "The main name of the admin console, the Pro variant",
      });
      BadgePart = ProBadgeText;
      break;
    }
    case "community": {
      title = intl.formatMessage({
        id: "product.title.community",
        defaultMessage: "Element Community",
        description:
          "The main name of the admin console, the Community variant",
      });
      BadgePart = CommunityBadgeText;
      break;
    }
    case "ti-m": {
      title = intl.formatMessage({
        id: "product.title.ti-m",
        defaultMessage: "Element TI-Messenger",
        description:
          "The main name of the admin console, the TI-Messenger variant",
      });
      BadgePart = TIMBadgeText;
      break;
    }

    default: {
      assertNever(variant);
    }
  }

  return (
    <div
      className={styles["element-logo-container"]}
      role="img"
      aria-label={title}
      data-variant={variant}
      {...props}
    >
      <ElementLogoPart aria-hidden="true" />
      <ElementTextPart aria-hidden="true" />
      <div className={styles["badge"]} aria-hidden="true">
        <BadgePart />
      </div>
    </div>
  );
};

